import React from "react";
import App from "../App";


const Form = ({setInputText}) => {

//Here I can write javascript code and function
    const inputTextHandler = (e) =>{
        console.log(e.target.value);
        setInputText(e.target.value);
        
    };
    const submitTodoHanler = (e)=>{
        e.preventDefault(); 
    }

    return(
        <form>
        <input onChange={inputTextHandler} type="text" classNameNameName="todo-input" />
        <button onClick={submitTodoHanler} classNameNameName="todo-button" type="submit">
            <i classNameNameName="fas fa-plus-square"></i>
        </button>
        <div classNameNameName="select">
            <select name="todos" classNameName="filter-todo">
                <option value="all">All</option>
                <option value="completed">Completed</option>
                <option value="uncompleted">Uncompleted</option>
            </select>
        </div>
    </form>


    );




}
   



export default Form;
